import React from 'react'
import {Card, CardContent, Typography} from '@material-ui/core'
import {Field, Form, Formik} from 'formik'
import {CheckboxWithLabel, TextField} from 'formik-material-ui'
import {mixed, number, object} from 'yup';

export default function NewEvent() {
    return(
            <Card>
                <CardContent>
                    <Formik 
                    validationSchema={object({
                        concurrency: mixed().when('closedSpace', {
                            is:true,
                            then: number().required().max(50, 'there cannot be more than 50 in a closed space due to covid 19 restrictions'),
                            otherwise: number().required()
                        })
                    })}
                    initialValues={{
                        title: '',
                        description: '',
                        closedSpace: false,
                        concurrency:0,
                        address: ''
                    }}
                    onSumbit={() => {}}>
                    <Form autocomplete="off"> 
                    <div>
                    <Field name= "title" component={TextField} label="Event Title"/>
                    <Field name= "description" component={TextField} label="Description"/>
                    <Field name= "closedSpace" type="checkbox" component={CheckboxWithLabel} label={{label: "does the event take place in a closed space?"}}/>
                    </div>

                    <div>
                    <Field name= "concurrency" type="number" component={TextField} label="Concurrency"/>
                    <Field name= "address" component={TextField} label="Event Address"/>
                    </div>

                    </Form>

                    </Formik>
                </CardContent>
            </Card>
    )
}
