import React from 'react'
import {Link} from 'react-router-dom';
//import { Table } from 'reactstrap';

const Events = () => {
    const [events, setEvent] = React.useState([])

    React.useEffect(() => {
        obtainEvents()
    }, [])

    const obtainEvents = async () => {
        const events = await fetch('https://www.cultura.gob.ar/api/v2.0/convocatorias/?limit=5&offset=5')
        .then(response => response.json())
        .then(data => data.results)
        .then(event => event.map(event => fetch(`https://www.cultura.gob.ar/api/v2.0/convocatorias/${event.id}/`)
        .then(response => response.json())))
        .then(data => data.result)

       // const particularEvents = events.map(event => fetch(`https://www.cultura.gob.ar/api/v2.0/convocatorias/${event.id}/`)
       // .then(response => response.json()))
        /*
        .then(data => data.map(event => fetch(`https://www.cultura.gob.ar/api/v2.0/convocatorias/${event.id}/`)
        .then(response => response.json())
        ))
*/



        //const events = await data.json()

        setEvent(events)
    }


    return (
        <div>
            <h1>{console.log(events)}</h1>


            <table class="table">
                <thead class="thead-dark">
                    <tr>
                        <th scope="col">Evento</th>
                        <th scope="col">Estado</th>
                        <th scope="col">Fecha Inicio</th>
                    </tr>
                </thead>
                <tbody>

    {events.map(
            event => 
                <tr key={event.id}>
                  <th scope='row'>{event.titulo}</th>
                  <th scope='row'>{event.estado}</th>
                  <th scope='row'>{event.fecha_inicio}</th>

                </tr>
            )}

  </tbody>
  </table>


        </div>
    )



    /*
    return (
        <div>
            <h1>Nosotros</h1>

            <ul>
            {
                equipo.map(item => (
                    <li key ={item.id}>
                        <Link to= {`/nosotros/${item.id}`}>
                        {item.name} - {item.expansion} 
                        </Link>
                        </li>
                ))
            }
            </ul>

        </div>
    )
*/
}

export default Events
