import React from 'react';
import {Formik, Form} from 'formik';
import {connect, useSelector} from 'react-redux'
import {TextField} from './TextField';
import * as Yup from 'yup';

function SignUpForm() {
  const validate = Yup.object().shape({
    firstName: Yup.string()
    .max(15, 'Must be 15 characters or less')
    .required('Required'),
    lastName: Yup.string()
    .max(20, 'Must be 20 characters or less')
    .required('Required'),
    email: Yup.string()
        .email("Incorrect email format")
        .required("Please enter a valid email"),
    password: Yup.string()
        .required("Enter a password")
        .min(8,"Password is too short - should be 8 characters minimum")
        .matches(/(?=.*[0-9])/,"Password must contain at least a number"),
    confirmPassword: Yup.string()
    .oneOf([Yup.ref('password'), null], 'Password must match')
})


    return (

      <div className="card text-center">
        <CardContent>
      <Formik
        initialValues={{
          firstName: '',
          lastName: '',
          email: '',
          password: '',
          confirmPassword: ''
        }}
        validationSchema ={validate}
        onSubmit={(values,{setSubmitting}) => {
          setSubmitting(true)
          console.log('hiii',values)
          //this.handleSubmit(values)
      }}
        >
        {({values, handleChange, handleBlur, handleSubmit}) => (
          <div>

            <h1 className="my-4 font-weight-bold-display-4">Sign Up</h1> 

            <form onSubmit={handleSubmit}>
            <TextField label="First Name" name="firstName" type="text" value={values.firstName} onChange={handleChange} onBlur={handleBlur}/>
            <TextField label="Last Name" name="lastName" type="text" value={values.lastName} onChange={handleChange} onBlur={handleBlur}/>
            <TextField label="Email" name="email" type="email" value={values.email} onChange={handleChange} onBlur={handleBlur}/>
            <TextField label="Password" name="password" type="password" value={values.password} onChange={handleChange} onBlur={handleBlur}/>
            <TextField label="Confirm Password" name="confirmPassword" value={values.confirmPassword} type="password" onChange={handleChange} onBlur={handleBlur}/>


          <button className="btn btn-dark mt-3" type="submit">Register</button>
          <button className="btn btn-danger mt-3 ml-3" type="reset">Reset</button>

          </form>
          </div>

        )

        }

      </Formik>
      </CardContent>
      </div>
    );

  }

export default SignUpForm;
