import { createStore} from 'redux';
//import authReducer from './reducers'


const initialState = {
    isAuthorized: "false"
  //  isAuthorized: localStorage.getItem('isAuthorized') || false,
  }

  const authReducer = (state = initialState, action) => {
    switch(action.type) {
      case 'LOGIN':
        return {
          ...state,
          isAuthorized: true,
        };
      case 'LOGOUT':
        return {
          ...state,
          isAuthorized: false,
        };
      default:
        return state;
    }
  }


export default createStore(authReducer);
