import React from 'react';
import {Formik, Form} from 'formik';
import {connect, useSelector, useDispatch} from 'react-redux'
import {TextField} from './TextField';
import {Redirect} from "react-router-dom";
import {useHistory} from "react-router-dom"
import * as Yup from 'yup';

function SignInForm() {

  const loginUser = useDispatch()
  let history = useHistory()
  const validate = Yup.object().shape({
    email: Yup.string()
        .email("Incorrect email format")
        .required("Please enter a valid email"),
    password: Yup.string()
        .required("Enter a password")
        .min(8,"Password is too short - should be 8 characters minimum")
        .matches(/(?=.*[0-9])/,"Password must contain at least a number"),

})

    /*
    function handleLogout() {
      props.signOutUser()
      // props.history.push(`/`)
      return <Redirect to="/"/>
    }
  */
    return (

      <div className="container mt-3">
      <Formik
        initialValues={{
          firstName: '',
          lastName: '',
          email: '',
          password: '',
          confirmPassword: ''
        }}
        validationSchema ={validate}
        onSubmit={(values,{setSubmitting}) => {
          setSubmitting(true)
          loginUser({type: 'LOGIN'});
          //this.handleSubmit(values)
          history.push('/')
         // <Redirect to={ '/games' }/>
      }}
        >
        {({values, handleChange, handleBlur, handleSubmit}) => (
          <div>

            <h1 className="my-4 font-weight-bold-display-4">Sign In</h1> 

            <form onSubmit={handleSubmit}>
            <TextField label="Email" name="email" type="email" value={values.email} onChange={handleChange} onBlur={handleBlur}/>
            <TextField label="Password" name="password" type="password" value={values.password} onChange={handleChange} onBlur={handleBlur}/>
            <TextField label="Confirm Password" name="confirmPassword" value={values.confirmPassword} type="password" onChange={handleChange} onBlur={handleBlur}/>

          <button className="btn btn-success mt-3" type="submit">Login</button>
          <button className="btn btn-danger mt-3 ml-3" type="reset">Reset</button>
          <p className="forgot-password text-right">
                                    Not a member yet? <a href="/sign_up">Sign up for free</a>
                                </p>
          </form>
          </div>

        )

        }

      </Formik>

      </div>
    );

  }





export default (SignInForm);
