import {useParams} from 'react-router-dom';

const Event = () => {

  //  console.log(useParams());

    const {id} = useParams();

  //  console.log(id);

    const [event, setEvent] = React.useState([])
    //const data = await fetch(`https://www.cultura.gob.ar/api/v2.0/convocatorias/${id}/`)
    React.useEffect(() => {
        const obtenerDatos = async () => {
            const data = await fetch(`https://www.cultura.gob.ar/api/v2.0/convocatorias/${id}/`)
            const event = await data.json()
            setEvent(event)

        }
        obtenerDatos()
    }, []) //el seguimiento va a estar dentro de este id

//[id]



    return (

        <div>
            <h3>{event.titulo}</h3>
        <div class="card" style="width: 18rem;">
        <img class="card-img-top" src={event.imagen} alt="Card image cap"/>
        <div class="card-body">
          <h5 class="card-title">{event.titulo}</h5>
          <p class="card-text">{event.bajada}</p>
        </div>
        <ul class="list-group list-group-flush">
          <li class="list-group-item">Cras justo odio</li>
          <li class="list-group-item">Dapibus ac facilisis in</li>
          <li class="list-group-item">Vestibulum at eros</li>
        </ul>
        <div class="card-body">
          <a href="#" class="card-link">Card link</a>
          <a href="#" class="card-link">Another link</a>
        </div>
      </div>
      </div>
    )

}

export default Event
